function atualizarLote(n,t){let i=document.getElementById("nome_"+n).value,r=document.getElementsByName("tel_"+n)[0].value,u=document.getElementById("contribui_"+n).checked;$.ajax({url:"ajax-lote.php",type:"get",dataType:"json",data:{id:n,nome:i,telefone:r,contribui:u,construido:!1,construindo:!1}}).done(function(){t&&document.location.reload()}).fail(function(n,t){displayMessage("Ocorreu um erro ao atualizar os dados do lote!.","Atualizar Lote","error",5);console.log("ERRO AO GRAVAR LOTE!"+t)})}