function inserirIndicacao(){let n=$("#tituloNovaIndicacao").val(),t=$("#descricaoNovaIndicacao").val(),i=getCustomer();if(t==""||t==null||t==undefined){displayMessage("Por favor informe a descrição da indicação.","Descrição da indicação","error",3);return}if(n==""||n==null||n==undefined){displayMessage("Por favor informe o título da indicação","Título da indicação","error",3);return}$("#btn-salvar").prop("disabled",!0);$("#btn-salvar").html("Salvando...");$("#btn-fechar").prop("disabled",!0);$.ajax({url:"ajax-indicacao.php",type:"get",dataType:"json",data:{acao:"i",titulo:n,texto:t,user:i.nome}}).done(function(){document.location.reload(!0)}).fail(function(){document.location.reload(!0)})}function limparDadosModalIndicacao(){$("#tituloNovaIndicacao").val("");$("#descricaoNovaIndicacao").val("")}function displayIndicacao(){let n=getCustomer();n?(document.getElementById("div-inserir-indicacao").style.display="block",document.getElementById("join-to-indicate").style.display="none"):(document.getElementById("div-inserir-indicacao").style.display="none",document.getElementById("join-to-indicate").style.display="block")}